-------------------------------------------------------------------------------------------------------------------------------------
-- Query to refresh records into table doz_mfm_customer.doz_findings.
-------------------------------------------------------------------------------------------------------------------------------------

USE doz_mfm_customer;

TRUNCATE TABLE doz_mfm_customer.doz_findings;

INSERT INTO doz_mfm_customer.doz_findings SELECT rndFnd.idRound AS idRound, rndTemp.RoundName AS RoundName, rnds.idRoundStatus AS idRoundStatus, rndFnd.idRoundLibrary AS idRoundLibrary, rndLbr.libraryName AS libraryName, rndFnd.idRoundLibraryItem AS idRoundLibraryItem, rndLbrItm.libraryitem AS libraryitem, rnds.dateCreated AS dateRoundCreated, rndLbrItm.displayorder AS displayorder, rnds.idCustomerSegment AS idCustomerSegment, lctn.IdCostCenter AS IdCostCenter, cstCntr1.CostCenterDescription AS CostCenterDescription, rnds.IdCostCenter AS IdDepartment, cstCntr2.CostCenterDescription AS DepartmentName, rnds.idLocation AS idLocation, rnds.idRoundTemplate AS idRoundTemplate, CASE WHEN rndFnd.idRoundStatus != 3 AND rndFnd.idRoundStatus != 2 THEN 1 ELSE NULL END AS zero_score, CASE WHEN rndFnd.idRoundStatus IS NULL OR   rndFnd.idRoundStatus = 3 OR   rndFnd.idRoundStatus = 2 THEN 1 ELSE NULL END AS one_score, rnds.deleted AS roundDeleted FROM dsz_mfm_cms.dsz_tblRoundFindings rndFnd  JOIN dsz_mfm_cms.dsz_tblRounds rnds  ON rndFnd.IDRound = rnds.IDRound JOIN dsz_mfm_cms.dsz_tblRoundLibraryItems rndLbrItm  ON rndFnd.IDRoundLibraryItem = rndLbrItm.IDRoundLibraryItem LEFT OUTER JOIN dsz_mfm_cms.dsz_tblRoundLibraries rndLbr  ON rndLbrItm.IDRoundLibrary = rndLbr.IDRoundLibrary JOIN dsz_mfm_cms.dsz_tblRoundTemplates rndTemp ON rnds.IDRoundTemplate = rndTemp.IDRoundTemplate LEFT OUTER JOIN dsz_mfm_cms.dsz_Location lctn  ON rnds.IDLocation = lctn.IDLocation LEFT OUTER JOIN dsz_mfm_cms.dsz_CostCenter cstCntr1 ON lctn.IDCostCenter = cstCntr1.IDCostCenter LEFT OUTER JOIN dsz_mfm_cms.dsz_CostCenter cstCntr2 ON rnds.IDCostCenter = cstCntr2.IDCostCenter WHERE rnds.idRoundStatus>0 AND rndTemp.RoundName like 'CSHA%';